pragma solidity ^0.4.24;

import "../network/SocialNetwork.sol";
import "../token/HeyToken.sol";
import "../ownership/Ownable.sol";

/**
 * @title Distributor
 * @dev A very primitive implementation of rewarding schemes for likes
 * @dev Issue: for now this smart contract uses its own wallet for
 * transfers. Give it rights to execute transactions directly from the pool.
 * @dev Issue: for now the distribution is directly proportional to the
 * number of likes since genesis. Make it not directly proportional, and make
 * it consider only the time window since last distribution occurred.
 * @dev Issue: for now there is no aggregation of rewards per user before
 * transactions occur. Hence if a user deserves rewards for 3 different posts,
 * she will receive 3 separate transactions. This is highly inefficient.
 */
contract Distributor is Ownable {
  SocialNetwork socialNetwork;
  HeyToken heyToken;

  event Catchall(uint slot);

  /**
   * @dev Constructor function
   * @param _socialNetworkAddress address The address of the already deployed social
   * network smart contract
   * @param _heyTokenAddress address The address of the ERC20 Hey token smart
   * contract, for which the distributor must then receive a wallet with some
   * tokens to perform the distribution
   */
  constructor (address _heyTokenAddress, address _socialNetworkAddress) public {
    heyToken = HeyToken(_heyTokenAddress);
    socialNetwork = SocialNetwork(_socialNetworkAddress);
  }

  function distributeRewards() onlyOwner public {
    uint numberOfPosts = socialNetwork.totalPosts();
    uint i = 0;

    // Loop through all posts, and for each do
    for (i; i < numberOfPosts; i++) {
      // Retrieve number of likes for the post
      uint numberOfLikes = socialNetwork.numberOfLikesPerPost(i);
      // Transfer an equivalent amount of HEY tokens to the post owner
      if (numberOfLikes > 0) {
        address beneficiary = socialNetwork.postOwner(i);
        uint256 reward = numberOfLikes * 1e18;
        // The distributor directly transfers from his own wallet
        heyToken.transfer(beneficiary, reward);
      }
    }
  }
}

