pragma solidity ^0.4.24;

/**
 * @title SocialNetwork
 * @dev A very primitive implementation of a social network, with posts and likes
 */
contract SocialNetwork {
    struct Post {
        string text;
    }

    // Mapping of owner by post
    mapping (uint => address) public postOwners;
    // Mapping of all posts for each owner
    mapping (address => uint[]) public postsFromAccount;
    // Mapping of all likes (referenced by their post id) for each liker
    mapping (address => uint[]) public postsLikedByAccount;
    // Mapping of all likers addresses for each post
    mapping (uint => address[]) public postLikers;
    // Mapping of likes per post for each liker - used to have a direct
    // data about whether a specific post has already been liked by a given
    // address. This is faster than checking if the postLikers array for
    // a post contains a given address (because there's no native Solidity
    // method to do this)
    mapping (address => mapping(uint => bool)) public postLikedByAccount;

    Post[] public posts;

    event NewPostAdded(uint postId, address owner, string text);
    event NewPostLiked(uint postId, address liker);

    function hasPosts() public view returns(bool _hasPosts) {
        _hasPosts = posts.length > 0;
    }

    function totalPosts() public view returns(uint _totalPosts) {
        _totalPosts = posts.length;
    }

    function lastPostId() public view returns(uint _postId) {
        _postId = posts.length - 1;
    }

    function hasLikes(uint _postId) public view returns(bool _hasLikes) {
        _hasLikes = postLikers[_postId].length > 0;
    }

    function likedBy(uint _postId, address _by) public view returns(bool _likedBy) {
        _likedBy = postLikedByAccount[_by][_postId];
    }

    function postOwner(uint _postId) public view returns(address _owner) {
        _owner = postOwners[_postId];
    }

    function numberOfLikesPerPost(uint _postId) public view returns(uint _numberOfLikes) {
        _numberOfLikes = postLikers[_postId].length;
    }

    function numberOfLikesPerPoster(address _poster) public view returns(uint _numberOfLikes) {
        uint[] storage postsIds = postsFromAccount[_poster];
        uint numberOfPosts = postsIds.length;
        _numberOfLikes = 0;

        uint256 i = 0;

        for (i; i < numberOfPosts; i++) {
              _numberOfLikes += numberOfLikesPerPost(i);
        }
    }

    function newPost(string _text) public {
        // Create a new post
        Post memory post = Post(_text);
        // Push the post in the memory array and record its id
        uint postId = posts.push(post) - 1;
        // Record ownership of the post
        postOwners[postId] = msg.sender;
        // Add the post to the list of posts of its creator
        postsFromAccount[msg.sender].push(postId);
        // Emit event
        emit NewPostAdded(postId, msg.sender, _text);
    }

    function newLike(uint _postId) public {
        // Cannot double-like a post
        require(!postLikedByAccount[msg.sender][_postId]);
        // Cannot like own post
        require(postOwners[_postId] != msg.sender);
        // Add the liker address to the list of likers of this post
        postLikers[_postId].push(msg.sender);
        // Add the post to the list of posts liked by the liker
        postsLikedByAccount[msg.sender].push(_postId);
        // Flag the post as liked for this liker
        postLikedByAccount[msg.sender][_postId] = true;
        // Emit event
        emit NewPostLiked(_postId, msg.sender);
    }
}
